<body class="bg-gray-100 min-h-screen flex items-center justify-center p-4">
    <div class="bg-white p-8 rounded-lg shadow-xl w-full max-w-2xl">
        <h1 class="text-3xl font-bold text-gray-800 mb-6 text-center">Text File Word Cross-Checker</h1>

        <!-- File Upload Section -->
        <div class="mb-6 space-y-4">
            <!-- Text Document File Input -->
            <div>
                <label for="docFileInput" class="block text-gray-700 text-sm font-medium mb-2">
                    Upload Text Document (e.g., from PDF conversion):
                </label>
                <input type="file" id="docFileInput" accept=".txt" class="hidden-input">
                <button id="selectDocButton" class="w-full bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-4 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-opacity-75 transition duration-200">
                    Select Text Document
                </button>
                <span id="docFileName" class="block text-gray-600 text-sm mt-2">No file selected</span>
            </div>

            <!-- Word List File Input -->
            <div>
                <label for="wordListFileInput" class="block text-gray-700 text-sm font-medium mb-2">
                    Upload Word List File (e.g., words.txt - format: word,expected_count):
                </label>
                <input type="file" id="wordListFileInput" accept=".txt,.csv" class="hidden-input">
                <button id="selectWordListButton" class="w-full bg-green-500 hover:bg-green-600 text-white font-semibold py-2 px-4 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-opacity-75 transition duration-200">
                    Select Word List
                </button>
                <span id="wordListFileName" class="block text-gray-600 text-sm mt-2">No file selected</span>
            </div>
        </div>

        <!-- Options -->
        <div class="mb-6 flex items-center justify-center space-x-6">
            <div class="flex items-center">
                <input type="checkbox" id="caseSensitiveCheckbox" class="form-checkbox h-4 w-4 text-blue-600 rounded focus:ring-blue-500">
                <label for="caseSensitiveCheckbox" class="ml-2 text-gray-700 text-sm">Case-sensitive search</label>
            </div>
            <!-- Whole Words Option -->
            <div class="flex items-center">
                <input type="checkbox" id="wholeWordsCheckbox" class="form-checkbox h-4 w-4 text-purple-600 rounded focus:ring-purple-500">
                <label for="wholeWordsCheckbox" class="ml-2 text-gray-700 text-sm">Match whole words only</label>
            </div>
        </div>

        <!-- Process Button -->
        <button id="processButton" class="w-full bg-purple-600 hover:bg-purple-700 text-white font-bold py-3 px-6 rounded-md shadow-lg focus:outline-none focus:ring-2 focus:ring-purple-500 focus:ring-opacity-75 transition duration-300 transform hover:scale-105">
            Cross-Check Words
        </button>

        <!-- Loading Indicator -->
        <div id="loadingIndicator" class="mt-6 text-center text-blue-500 font-medium hidden">
            Processing... Please wait.
        </div>

        <!-- Results Display Area -->
        <div id="resultsArea" class="mt-8 p-6 bg-gray-50 rounded-lg border border-gray-200 shadow-inner hidden">
            <h2 class="text-xl font-semibold text-gray-800 mb-4">Comparison Results:</h2>
            <div id="resultsContent" class="text-gray-700 whitespace-pre-wrap">
                <!-- Results will be injected here -->
            </div>
            <!-- Export Buttons (Updated to include CSV) -->
            <div class="flex space-x-4 mt-4">
                <button id="exportResultsButton" class="flex-1 bg-gray-700 hover:bg-gray-800 text-white font-semibold py-2 px-4 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-gray-500 focus:ring-opacity-75 transition duration-200">
                    Export Results to TXT
                </button>
                <button id="exportResultsCsvButton" class="flex-1 bg-teal-600 hover:bg-teal-700 text-white font-semibold py-2 px-4 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-teal-500 focus:ring-opacity-75 transition duration-200">
                    Export Results to CSV
                </button>
            </div>
        </div>

        <!-- Error Message Area -->
        <div id="errorArea" class="mt-4 p-4 bg-red-100 border border-red-400 text-red-700 rounded-md hidden">
            <p id="errorMessage"></p>
        </div>
    </div>

    <script type="module">
        // Global variable to store the raw results array for CSV export
        let lastResults = []; 

        // Get DOM elements
        const docFileInput = document.getElementById('docFileInput');
        const wordListFileInput = document.getElementById('wordListFileInput');
        const selectDocButton = document.getElementById('selectDocButton');
        const selectWordListButton = document.getElementById('selectWordListButton');
        const docFileNameSpan = document.getElementById('docFileName');
        const wordListFileNameSpan = document.getElementById('wordListFileName');
        const caseSensitiveCheckbox = document.getElementById('caseSensitiveCheckbox');
        const wholeWordsCheckbox = document.getElementById('wholeWordsCheckbox');
        const processButton = document.getElementById('processButton');
        const loadingIndicator = document.getElementById('loadingIndicator');
        const resultsArea = document.getElementById('resultsArea');
        const resultsContent = document.getElementById('resultsContent');
        const exportResultsButton = document.getElementById('exportResultsButton'); // TXT Export
        const exportResultsCsvButton = document.getElementById('exportResultsCsvButton'); // CSV Export
        const errorArea = document.getElementById('errorArea');
        const errorMessage = document.getElementById('errorMessage');

        let docFile = null;
        let wordListFile = null;

        // --- Event Listeners for File Selection Buttons ---
        selectDocButton.addEventListener('click', () => docFileInput.click());
        selectWordListButton.addEventListener('click', () => wordListFileInput.click());

        docFileInput.addEventListener('change', (event) => {
            docFile = event.target.files[0];
            if (docFile) {
                docFileNameSpan.textContent = `Selected: ${docFile.name}`;
            } else {
                docFileNameSpan.textContent = 'No file selected';
            }
            hideResultsAndError();
        });

        wordListFileInput.addEventListener('change', (event) => {
            wordListFile = event.target.files[0];
            if (wordListFile) {
                wordListFileNameSpan.textContent = `Selected: ${wordListFile.name}`;
            } else {
                wordListFileNameSpan.textContent = 'No file selected';
            }
            hideResultsAndError();
        });

        // --- Process Button Click Listener ---
        processButton.addEventListener('click', processFiles);

        // --- Export Button Click Listeners ---
        exportResultsButton.addEventListener('click', exportResultsTxt);
        exportResultsCsvButton.addEventListener('click', exportResultsCsv);

        // --- Helper Functions for UI State ---
        function showLoading() {
            loadingIndicator.classList.remove('hidden');
            resultsArea.classList.add('hidden');
            errorArea.classList.add('hidden');
            processButton.disabled = true;
        }

        function hideLoading() {
            loadingIndicator.classList.add('hidden');
            processButton.disabled = false;
        }

        function showResults(content) {
            resultsContent.textContent = content;
            resultsArea.classList.remove('hidden');
        }

        function showError(message) {
            errorMessage.textContent = message;
            errorArea.classList.remove('hidden');
        }

        function hideResultsAndError() {
            resultsArea.classList.add('hidden');
            errorArea.classList.add('hidden');
        }

        // --- Main Processing Function ---
        async function processFiles() {
            hideResultsAndError();
            if (!docFile) {
                showError('Please select a Text Document.');
                return;
            }
            if (!wordListFile) {
                showError('Please select a Word List file.');
                return;
            }

            showLoading();

            try {
                // Read Text Document content directly here
                const docText = await readFileAsText(docFile);

                // Read Word List content
                const wordListContent = await readFileAsText(wordListFile);
                const wordList = parseWordList(wordListContent);

                // Perform cross-check, passing both options
                const results = performCrossCheck(
                    docText, 
                    wordList, 
                    caseSensitiveCheckbox.checked,
                    wholeWordsCheckbox.checked
                );

                // Store raw results globally for CSV export
                lastResults = results; 

                // Display formatted TXT results
                showResults(formatResultsTxt(results));

            } catch (error) {
                console.error('Error during processing:', error);
                showError(`An error occurred: ${error.message || error}`);
            } finally {
                hideLoading();
            }
        }

        // --- Download Utility ---
        function downloadFile(content, filename, mimeType) {
            // Create a Blob from the content
            const blob = new Blob([content], { type: mimeType });

            // Create a link element
            const a = document.createElement('a');
            a.href = URL.createObjectURL(blob);
            a.download = filename; 

            // Append to body, click, and remove
            document.body.appendChild(a);
            a.click();
            document.body.removeChild(a);

            // Revoke the object URL to free up memory
            URL.revokeObjectURL(a.href);
        }

        // --- Export Results Function (TXT) ---
        function exportResultsTxt() {
            const resultsText = resultsContent.textContent;
            if (!resultsText) {
                showError('No results to export. Please run the cross-check first.');
                return;
            }
            downloadFile(resultsText, 'word_cross_check_results.txt', 'text/plain;charset=utf-8');
        }

        // --- Export Results Function (CSV) ---
        function exportResultsCsv() {
            if (lastResults.length === 0) {
                showError('No results to export. Please run the cross-check first.');
                return;
            }

            const csvContent = formatResultsCsv(lastResults);
            downloadFile(csvContent, 'word_cross_check_results.csv', 'text/csv;charset=utf-8');
        }

        // --- File Reading Utility ---
        function readFileAsText(file) {
            return new Promise((resolve, reject) => {
                const reader = new FileReader();
                reader.onload = (event) => resolve(event.target.result);
                reader.onerror = (event) => reject(new Error(`Failed to read file: ${file.name}`));
                reader.readAsText(file);
            });
        }

        // --- Word List Parsing ---
        function parseWordList(content) {
            const lines = content.split('\n');
            const wordList = [];
            lines.forEach(line => {
                const trimmedLine = line.trim();
                if (trimmedLine) {
                    const parts = trimmedLine.split(',');
                    if (parts.length >= 2) {
                        const word = parts[0].trim();
                        const expectedCount = parseInt(parts[1].trim(), 10);
                        if (word && !isNaN(expectedCount)) {
                            wordList.push({ word, expectedCount });
                        } else {
                            console.warn(`Skipping malformed word list line (invalid word or count): "${line}"`);
                        }
                    } else {
                        console.warn(`Skipping malformed word list line (missing comma/count): "${line}"`);
                    }
                }
            });
            return wordList;
        }

        // --- Core Cross-Check Logic ---
        function performCrossCheck(docText, wordList, isCaseSensitive, isWholeWords) {
            const results = [];

            wordList.forEach(({ word, expectedCount }) => {
                let actualCount = 0;

                if (isWholeWords) {
                    // Whole Word Matching (Regex)
                    const flags = 'g' + (isCaseSensitive ? '' : 'i'); // 'g' for global, 'i' for ignore case

                    // Escape special regex characters in the search target word
                    const escapedWord = word.replace(/[.*+?^${}()|[\]\\]/g, '\\$&');

                    // Pattern uses \b for word boundaries to ensure whole word match
                    const pattern = `\\b${escapedWord}\\b`;

                    try {
                        const regex = new RegExp(pattern, flags);
                        // Use matchAll (modern, efficient) to count occurrences
                        actualCount = Array.from(docText.matchAll(regex)).length;
                    } catch (e) {
                         console.error('Regex error for word:', word, e);
                         // Fallback to substring counting if regex fails
                         const processedDocText = isCaseSensitive ? docText : docText.toLowerCase();
                         const searchTarget = isCaseSensitive ? word : word.toLowerCase();

                         let lastIndex = -1;
                         while ((lastIndex = processedDocText.indexOf(searchTarget, lastIndex + 1)) !== -1) {
                             actualCount++;
                         }
                    }

                } else {
                    // Substring Matching (Original indexOf logic)
                    // Must preprocess text and search term for case insensitivity
                    const processedDocText = isCaseSensitive ? docText : docText.toLowerCase();
                    const searchTarget = isCaseSensitive ? word : word.toLowerCase();

                    let lastIndex = -1;
                    while ((lastIndex = processedDocText.indexOf(searchTarget, lastIndex + 1)) !== -1) {
                        actualCount++;
                    }
                }

                let status;
                if (actualCount === expectedCount) {
                    status = 'MATCHED';
                } else {
                    status = 'MISMATCH';
                }

                results.push({
                    word: word,
                    expectedCount: expectedCount,
                    actualCount: actualCount,
                    status: status
                });
            });
            return results;
        }

        // --- Format Results for CSV ---
        function formatResultsCsv(results) {
            // CSV Header
            const header = "Word,Expected Count,Actual Count,Status\n";

            // CSV Rows
            const rows = results.map(r => {
                // Escape double quotes within the word for CSV compatibility
                const escapedWord = r.word.replace(/"/g, '""');
                return `"${escapedWord}",${r.expectedCount},${r.actualCount},${r.status}`;
            }).join('\n');

            return header + rows;
        }

        // --- Format Results for Display (TXT) ---
        function formatResultsTxt(results) {
            let output = '';
            const matched = results.filter(r => r.status === 'MATCHED');
            const mismatched = results.filter(r => r.status === 'MISMATCH');
            const lessThanExpected = mismatched.filter(r => r.actualCount < r.expectedCount);
            const moreThanExpected = mismatched.filter(r => r.actualCount > r.expectedCount);

            output += `--- Words Matched Expected Count (${matched.length}) ---\n`;
            if (matched.length > 0) {
                matched.forEach(r => {
                    output += `- "${r.word}" (Found ${r.actualCount} times)\n`;
                });
            } else {
                output += 'No words matched their expected count.\n';
            }

            output += `\n--- Words Mismatched Expected Count (Lesser than Expected - ${lessThanExpected.length}) ---\n`;
            if (lessThanExpected.length > 0) {
                lessThanExpected.forEach(r => {
                    output += `- "${r.word}" (Expected ${r.expectedCount}, Found ${r.actualCount} times)\n`;
                });
            } else {
                output += 'All words found were either equal to or greater than the expected count.\n';
            }

            output += `\n--- Words Mismatched Expected Count (More than Expected - ${moreThanExpected.length}) ---\n`;
            if (moreThanExpected.length > 0) {
                moreThanExpected.forEach(r => {
                    output += `- "${r.word}" (Expected ${r.expectedCount}, Found ${r.actualCount} times)\n`;
                });
            } else {
                output += 'All words found were either equal to or less than the expected count.\n';
            }

            return output;
        }
    </script>
</body>
</html>

